examples.code:
program HelloWorld;

begin
  WriteLn('Hello, World!');
end.

program MultipleLines;

begin
  WriteLn('Welcome to DWScript!');
  WriteLn('This is line 2');
  WriteLn('This is line 3');
end.

program WriteVsWriteLn;

begin
  Write('Hello');
  Write(' ');
  Write('World');
  WriteLn('!');
  WriteLn('Next line');
end.

