examples.code:
program IntegerExample;

var
  age: Integer;
  year: Integer;

begin
  age := 25;
  year := 2024;

  WriteLn('Age: ', age);
  WriteLn('Year: ', year);
end.

program StringExample;

var
  name: String;
  greeting: String;

begin
  name := 'Alice';
  greeting := 'Hello, ' + name + '!';

  WriteLn(greeting);
end.

program MultipleTypes;

var
  name: String;
  age: Integer;
  height: Real;
  isStudent: Boolean;

begin
  name := 'Bob';
  age := 20;
  height := 1.75;
  isStudent := True;

  WriteLn('Name: ', name);
  WriteLn('Age: ', age);
  WriteLn('Height: ', height, 'm');
  WriteLn('Student: ', isStudent);
end.

