begin
  name := 'Sarah';
  age := 22;
  favoriteColor := 'Blue';
  favoriteNumber := 7;

  WriteLn('Name: ', name);
  WriteLn('Age: ', age);
  WriteLn('Favorite Color: ', favoriteColor);
  WriteLn('Favorite Number: ', favoriteNumber);
end.

