program ArithmeticDemo;

var
  a, b: Integer;
  result: Float;

begin
  a := 10;
  b := 3;

  WriteLn('Addition: ', a + b);        // 13
  WriteLn('Subtraction: ', a - b);     // 7
  WriteLn('Multiplication: ', a * b);  // 30
  WriteLn('Division: ', a / b);        // 3.333...
  WriteLn('Integer division: ', a div b); // 3
  WriteLn('Modulus (remainder): ', a mod b); // 1
end.

program ComparisonDemo;

var
  x, y: Integer;

begin
  x := 5;
  y := 10;

  WriteLn('x = y: ', x = y);    // False
  WriteLn('x <> y: ', x <> y);  // True
  WriteLn('x < y: ', x < y);    // True
  WriteLn('x > y: ', x > y);    // False
  WriteLn('x <= 5: ', x <= 5);  // True
  WriteLn('y >= 10: ', y >= 10); // True
end.

program LogicalDemo;

var
  age: Integer;
  hasLicense: Boolean;

begin
  age := 18;
  hasLicense := True;

  WriteLn('Can drive: ', (age >= 18) and hasLicense);  // True
  WriteLn('Teen or adult: ', (age >= 13) or (age >= 18)); // True
  WriteLn('Not a minor: ', not (age < 18)); // True

  // More complex logic
  WriteLn('Senior discount: ', (age >= 65) or (age < 12)); // False
end.

program PrecedenceDemo;

var
  result: Integer;

begin
  // Without parentheses (follows precedence)
  result := 2 + 3 * 4;  // 3*4 first, then +2
  WriteLn('2 + 3 * 4 = ', result);  // 14

  // With parentheses (forces order)
  result := (2 + 3) * 4;  // 2+3 first, then *4
  WriteLn('(2 + 3) * 4 = ', result);  // 20

  // Complex expression
  result := 10 + 5 * 2 - 8 div 4;  // 5*2=10, 8div4=2, then 10+10-2
  WriteLn('10 + 5 * 2 - 8 div 4 = ', result);  // 18
end.

