program TempConverter;

var
  celsius: Float;
  fahrenheit: Float;

begin
  celsius := 25.0;
  // Calculate fahrenheit here

  WriteLn('Celsius: ', celsius);
  WriteLn('Fahrenheit: ', fahrenheit);
end.

exercises.solution:
program TempConverter;

var
  celsius: Float;
  fahrenheit: Float;

begin
  celsius := 25.0;
  fahrenheit := celsius * 9 / 5 + 32;

  WriteLn('Celsius: ', celsius);
  WriteLn('Fahrenheit: ', fahrenheit);
end.

