exercises.solution:
program VoteEligibility;

var
  age: Integer;
  hasRegistered: Boolean;
  canVote: Boolean;

begin
  age := 20;
  hasRegistered := True;
  canVote := (age >= 18) and hasRegistered;

  WriteLn('Age: ', age);
  WriteLn('Registered: ', hasRegistered);
  WriteLn('Can vote: ', canVote);
end.

