exercises.solution:
program EvenOdd;

var
  number: Integer;
  isEven: Boolean;

begin
  number := 42;
  isEven := (number mod 2) = 0;

  WriteLn('Number: ', number);
  WriteLn('Is even: ', isEven);
end.
