program GreetUser;

var
  name: String;

begin
  WriteLn('What is your name?');
  ReadLn(name);
  WriteLn('Hello, ', name, '! Welcome to DWScript!');
end.

program SquareNumber;

var
  input: String;
  number: Integer;
  square: Integer;

begin
  WriteLn('Enter a number:');
  ReadLn(input);
  number := StrToInt(input);
  square := number * number;

  WriteLn('The square of ', number, ' is ', square);
end.

program Calculator;

var
  input1, input2: String;
  num1, num2: Float;
  sum: Float;

begin
  WriteLn('Simple Addition Calculator');
  WriteLn('Enter first number:');
  ReadLn(input1);

  WriteLn('Enter second number:');
  ReadLn(input2);

  num1 := StrToFloat(input1);
  num2 := StrToFloat(input2);
  sum := num1 + num2;

  WriteLn(num1, ' + ', num2, ' = ', sum);
end.

program SimpleChat;

var
  name, color: String;
  age: Integer;
  ageStr: String;

begin
  WriteLn('Hi! I\'d like to get to know you.');
  WriteLn('');

  WriteLn('What\'s your name?');
  ReadLn(name);

  WriteLn('How old are you, ', name, '?');
  ReadLn(ageStr);
  age := StrToInt(ageStr);

  WriteLn('What\'s your favorite color?');
  ReadLn(color);

  WriteLn('');
  WriteLn('Nice to meet you, ', name, '!');
  WriteLn('You are ', age, ' years old and you love ', color, '.');
end.

