exercises.solution:
program TempConverter;

var
  celsiusStr: String;
  celsius, fahrenheit: Float;

begin
  WriteLn('Temperature Converter (Celsius to Fahrenheit)');
  WriteLn('Enter temperature in Celsius:');
  ReadLn(celsiusStr);

  celsius := StrToFloat(celsiusStr);
  fahrenheit := celsius * 9 / 5 + 32;

  WriteLn(celsius, '°C = ', fahrenheit, '°F');
end.

