exercises.solution:
program RectangleArea;

var
  widthStr, heightStr: String;
  width, height, area: Float;

begin
  WriteLn('Rectangle Area Calculator');

  WriteLn('Enter width:');
  ReadLn(widthStr);

  WriteLn('Enter height:');
  ReadLn(heightStr);

  width := StrToFloat(widthStr);
  height := StrToFloat(heightStr);
  area := width * height;

  WriteLn('The area of a rectangle with width ', width);
  WriteLn('and height ', height, ' is ', area);
end.

