exercises.solution:
program PersonalInfo;

var
  name, hobby, ageStr: String;
  age: Integer;

begin
  WriteLn('Tell me about yourself!');

  WriteLn('What is your name?');
  ReadLn(name);

  WriteLn('How old are you?');
  ReadLn(ageStr);
  age := StrToInt(ageStr);

  WriteLn('What is your favorite hobby?');
  ReadLn(hobby);

  WriteLn('');
  WriteLn('Summary:');
  WriteLn(name, ' is ', age, ' years old and enjoys ', hobby, '.');
end.
