program SimpleIf;

var
  age: Integer;

begin
  age := 20;

  if age >= 18 then
    WriteLn('You are an adult.');

  WriteLn('Program finished.');
end.

program IfElse;

var
  temperature: Integer;

begin
  temperature := 25;

  if temperature > 30 then
    WriteLn('It\'s hot outside!')
  else
    WriteLn('The temperature is comfortable.');
end.

program MultipleStatements;

var
  balance: Float;
  amount: Float;

begin
  balance := 100.0;
  amount := 30.0;

  WriteLn('Current balance: $', balance);
  WriteLn('Withdrawal amount: $', amount);

  if balance >= amount then
  begin
    balance := balance - amount;
    WriteLn('Withdrawal successful!');
    WriteLn('New balance: $', balance);
  end
  else
  begin
    WriteLn('Insufficient funds!');
    WriteLn('Balance remains: $', balance);
  end;
end.

program NestedIf;

var
  age: Integer;
  hasLicense: Boolean;

begin
  age := 20;
  hasLicense := True;

  if age >= 16 then
  begin
    if hasLicense then
      WriteLn('You can drive!')
    else
      WriteLn('You need to get a license first.');
  end
  else
    WriteLn('You are too young to drive.');
end.

program CombinedConditions;

var
  age: Integer;
  isStudent: Boolean;
  ticketPrice: Float;

begin
  age := 15;
  isStudent := True;

  if (age < 18) or isStudent then
  begin
    ticketPrice := 8.0;
    WriteLn('Discounted ticket: $', ticketPrice);
  end
  else
  begin
    ticketPrice := 12.0;
    WriteLn('Regular ticket: $', ticketPrice);
  end;
end.

