var
  year: Integer;

begin
  year := 2024;

  // Your code here

end.

exercises.solution:
program LeapYear;

var
  year: Integer;
  isLeap: Boolean;

begin
  year := 2024;

  isLeap := (year mod 4 = 0) and ((year mod 100 <> 0) or (year mod 400 = 0));

  if isLeap then
    WriteLn(year, ' is a leap year')
  else
    WriteLn(year, ' is not a leap year');
end.
