program BasicCase;

var
  day: Integer;

begin
  day := 3;

  case day of
    1: WriteLn('Monday');
    2: WriteLn('Tuesday');
    3: WriteLn('Wednesday');
    4: WriteLn('Thursday');
    5: WriteLn('Friday');
    6: WriteLn('Saturday');
    7: WriteLn('Sunday');
  else
    WriteLn('Invalid day');
  end;
end.

program DayType;

var
  day: Integer;

begin
  day := 6;

  case day of
    1, 2, 3, 4, 5: WriteLn('Weekday - time to work!');
    6, 7: WriteLn('Weekend - time to relax!');
  else
    WriteLn('Invalid day number');
  end;
end.

program GradeCategory;

var
  score: Integer;

begin
  score := 85;

  case score of
    90..100: WriteLn('Excellent! Grade: A');
    80..89:  WriteLn('Good job! Grade: B');
    70..79:  WriteLn('Fair. Grade: C');
    60..69:  WriteLn('Needs improvement. Grade: D');
    0..59:   WriteLn('Failed. Grade: F');
  else
    WriteLn('Invalid score');
  end;
end.

program MenuSystem;

var
  choice: Integer;

begin
  choice := 2;

  WriteLn('Menu Choice: ', choice);
  WriteLn('');

  case choice of
    1: begin
         WriteLn('You selected: New File');
         WriteLn('Creating new file...');
       end;
    2: begin
         WriteLn('You selected: Open File');
         WriteLn('Opening file browser...');
       end;
    3: begin
         WriteLn('You selected: Save File');
         WriteLn('Saving current file...');
       end;
    4: WriteLn('Exiting program...');
  else
    WriteLn('Invalid choice!');
  end;
end.

program VowelChecker;

var
  letter: Char;

begin
  letter := 'e';

  case letter of
    'a', 'e', 'i', 'o', 'u': WriteLn(letter, ' is a vowel');
    'A', 'E', 'I', 'O', 'U': WriteLn(letter, ' is a vowel (uppercase)');
  else
    WriteLn(letter, ' is a consonant');
  end;
end.

program Calculator;

var
  num1, num2: Float;
  operation: Char;
  result: Float;

begin
  num1 := 10.0;
  num2 := 3.0;
  operation := '+';

  case operation of
    '+': begin
           result := num1 + num2;
           WriteLn(num1, ' + ', num2, ' = ', result);
         end;
    '-': begin
           result := num1 - num2;
           WriteLn(num1, ' - ', num2, ' = ', result);
         end;
    '*': begin
           result := num1 * num2;
           WriteLn(num1, ' * ', num2, ' = ', result);
         end;
    '/': begin
           if num2 <> 0 then
           begin
             result := num1 / num2;
             WriteLn(num1, ' / ', num2, ' = ', result);
           end
           else
             WriteLn('Error: Division by zero!');
         end;
  else
    WriteLn('Invalid operation: ', operation);
  end;
end.

