program TrafficLight;

var
  color: Char;

begin
  color := 'G';

  case color of
    'R', 'r': WriteLn('STOP!');
    'Y', 'y': WriteLn('Slow down, prepare to stop');
    'G', 'g': WriteLn('GO!');
  else
    WriteLn('Invalid traffic light color');
  end;
end.

