program BasicWhile;

var
  count: Integer;

begin
  count := 1;

  while count <= 5 do
  begin
    WriteLn('Count: ', count);
    count := count + 1;
  end;

  WriteLn('Done!');
end.

program Countdown;

var
  num: Integer;

begin
  num := 10;

  while num > 0 do
  begin
    WriteLn(num);
    num := num - 1;
  end;

  WriteLn('Blastoff!');
end.

program SumNumbers;

var
  num: Integer;
  sum: Integer;

begin
  num := 1;
  sum := 0;

  while num <= 10 do
  begin
    sum := sum + num;
    num := num + 1;
  end;

  WriteLn('Sum of 1 to 10 is: ', sum);
end.

program InputValidation;

var
  input: String;
  age: Integer;
  valid: Boolean;

begin
  valid := False;

  while not valid do
  begin
    WriteLn('Enter your age (18-100):');
    ReadLn(input);
    age := StrToInt(input);

    if (age >= 18) and (age <= 100) then
      valid := True
    else
      WriteLn('Invalid age! Try again.');
  end;

  WriteLn('Thank you! Your age is ', age);
end.

program PowerOfTwo;

var
  n: Integer;
  count: Integer;
  result: Integer;

begin
  n := 5;  // Calculate 2^5
  count := 0;
  result := 1;

  while count < n do
  begin
    result := result * 2;
    count := count + 1;
  end;

  WriteLn('2^', n, ' = ', result);
end.

program NestedWhile;

var
  row, col: Integer;

begin
  row := 1;

  while row <= 3 do
  begin
    col := 1;
    while col <= 5 do
    begin
      Write('* ');
      col := col + 1;
    end;
    WriteLn('');
    row := row + 1;
  end;
end.

