program MultiplicationTable;

var
  i: Integer;

begin
  WriteLn('Multiplication table for 7:');
  i := 1;

  while i <= 10 do
  begin
    WriteLn('7 x ', i, ' = ', 7 * i);
    i := i + 1;
  end;
end.

