exercises.solution:
program Factorial;

var
  n: Integer;
  result: Integer;
  count: Integer;

begin
  n := 5;
  result := 1;
  count := n;

  while count > 0 do
  begin
    result := result * count;
    count := count - 1;
  end;

  WriteLn('Factorial of ', n, ' is: ', result);
end.

