exercises.solution:
program GuessingGame;

var
  secret: Integer;
  guess: Integer;
  guessStr: String;
  found: Boolean;

begin
  secret := 42;
  found := False;

  WriteLn('Guess the number (1-100):');

  while not found do
  begin
    ReadLn(guessStr);
    guess := StrToInt(guessStr);

    if guess = secret then
    begin
      WriteLn('Correct! The number was ', secret);
      found := True;
    end
    else if guess < secret then
      WriteLn('Too low! Try again:')
    else
      WriteLn('Too high! Try again:');
  end;
end.

