exercises.solution:
program SumEven;

var
  num: Integer;
  sum: Integer;

begin
  num := 2;
  sum := 0;

  while num <= 20 do
  begin
    sum := sum + num;
    num := num + 2;  // Skip to next even number
  end;

  WriteLn('Sum of even numbers 2-20: ', sum);
end.
