examples.code:
program BasicFor;

var
  i: Integer;

begin
  for i := 1 to 5 do
    WriteLn('Count: ', i);

  WriteLn('Done!');
end.

program Countdown;

var
  i: Integer;

begin
  WriteLn('Countdown:');

  for i := 10 downto 1 do
    WriteLn(i);

  WriteLn('Blastoff!');
end.

program ForMultiple;

var
  i: Integer;
  square: Integer;

begin
  for i := 1 to 5 do
  begin
    square := i * i;
    WriteLn(i, ' squared is ', square);
  end;
end.

program SumNumbers;

var
  i: Integer;
  sum: Integer;

begin
  sum := 0;

  for i := 1 to 100 do
    sum := sum + i;

  WriteLn('Sum of 1 to 100: ', sum);
end.

program TimesTable;

var
  i: Integer;
  multiplier: Integer;

begin
  multiplier := 7;
  WriteLn('Multiplication table for ', multiplier, ':');
  WriteLn('');

  for i := 1 to 10 do
    WriteLn(multiplier, ' x ', i, ' = ', multiplier * i);
end.

program MultiplicationGrid;

var
  row, col: Integer;

begin
  WriteLn('Multiplication Grid (1-5):');
  WriteLn('');

  for row := 1 to 5 do
  begin
    for col := 1 to 5 do
      Write(row * col, ' ');
    WriteLn('');
  end;
end.

program Triangle;

var
  row, col: Integer;

begin
  for row := 1 to 5 do
  begin
    for col := 1 to row do
      Write('* ');
    WriteLn('');
  end;
end.

