program RightTriangle;

var
  row, spaces, stars: Integer;

begin
  for row := 1 to 5 do
  begin
    // Print spaces
    for spaces := 1 to (5 - row) do
      Write(' ');

    // Print stars
    for stars := 1 to row do
      Write('*');

    WriteLn('');
  end;
end.

