exercises.solution:
program SumOfSquares;

var
  i: Integer;
  sum: Integer;

begin
  sum := 0;

  for i := 1 to 10 do
    sum := sum + (i * i);

  WriteLn('Sum of squares 1-10: ', sum);
end.
