program BasicBreak;

var
  i: Integer;

begin
  for i := 1 to 10 do
  begin
    if i = 6 then
      break;  // Exit loop when i reaches 6

    WriteLn('i = ', i);
  end;

  WriteLn('Loop finished, i = ', i);
end.

program SearchWithBreak;

var
  i: Integer;
  target: Integer;
  found: Boolean;

begin
  target := 7;
  i := 1;
  found := False;

  while i <= 10 do
  begin
    WriteLn('Checking ', i);

    if i = target then
    begin
      found := True;
      break;  // Found it, exit loop
    end;

    i := i + 1;
  end;

  if found then
    WriteLn('Found ', target, '!')
  else
    WriteLn('Not found');
end.

program BasicContinue;

var
  i: Integer;

begin
  for i := 1 to 10 do
  begin
    if (i mod 2) = 0 then
      continue;  // Skip even numbers

    WriteLn('Odd number: ', i);
  end;
end.

program ContinueWhile;

var
  i: Integer;

begin
  i := 0;

  while i < 10 do
  begin
    i := i + 1;

    if (i mod 3) = 0 then
      continue;  // Skip multiples of 3

    WriteLn(i);
  end;
end.

program BreakOnInvalid;

var
  i: Integer;
  num: Integer;
  numStr: String;
  sum: Integer;

begin
  sum := 0;

  WriteLn('Enter 5 positive numbers:');

  for i := 1 to 5 do
  begin
    Write('Number ', i, ': ');
    ReadLn(numStr);
    num := StrToInt(numStr);

    if num <= 0 then
    begin
      WriteLn('Invalid! Must be positive. Stopping.');
      break;
    end;

    sum := sum + num;
  end;

  WriteLn('Sum: ', sum);
end.

program FilterData;

var
  i: Integer;
  value: Integer;
  sum: Integer;

begin
  sum := 0;

  WriteLn('Processing values 1-10, skipping multiples of 5:');

  for i := 1 to 10 do
  begin
    value := i;

    // Skip multiples of 5
    if (value mod 5) = 0 then
    begin
      WriteLn('Skipping ', value);
      continue;
    end;

    WriteLn('Adding ', value);
    sum := sum + value;
  end;

  WriteLn('Sum: ', sum);
end.

program NestedBreak;

var
  row, col: Integer;
  found: Boolean;

begin
  found := False;

  for row := 1 to 5 do
  begin
    if found then
      break;  // Exit outer loop

    for col := 1 to 5 do
    begin
      if (row * col) = 12 then
      begin
        WriteLn('Found 12 at row=', row, ', col=', col);
        found := True;
        break;  // Exit inner loop
      end;
    end;
  end;
end.

