program FirstDivisor;

var
  i: Integer;

begin
  WriteLn('Finding first divisor of 100:');

  for i := 2 to 10 do
  begin
    if (100 mod i) = 0 then
    begin
      WriteLn('First divisor: ', i);
      break;
    end;
  end;
end.

