program SkipNegative;

var
  i: Integer;
  sum: Integer;
  values: array[1..6] of Integer;

begin
  // Simulated array values
  values[1] := 5;
  values[2] := -3;
  values[3] := 8;
  values[4] := -1;
  values[5] := 12;
  values[6] := 4;

  sum := 0;

  for i := 1 to 6 do
  begin
    if values[i] < 0 then
      continue;  // Skip negative

    sum := sum + values[i];
  end;

  WriteLn('Sum of positive numbers: ', sum);
end.

