program SentinelSum;

var
  num: Integer;
  numStr: String;
  sum: Integer;
  i: Integer;

begin
  sum := 0;
  WriteLn('Enter numbers (-1 to stop):');

  for i := 1 to 100 do  // Max 100 numbers
  begin
    ReadLn(numStr);
    num := StrToInt(numStr);

    if num = -1 then
      break;  // Stop on sentinel

    sum := sum + num;
  end;

  WriteLn('Sum: ', sum);
end.

