exercises.solution:
program PrimeCheck;

var
  number: Integer;
  i: Integer;
  isPrime: Boolean;

begin
  number := 17;
  isPrime := True;

  for i := 2 to (number - 1) do
  begin
    if (number mod i) = 0 then
    begin
      isPrime := False;
      break;  // Found a divisor, not prime
    end;
  end;

  if isPrime then
    WriteLn(number, ' is prime')
  else
    WriteLn(number, ' is not prime');
end.
