examples.code:
program SimpleProcedure;

procedure SayHello;
begin
  WriteLn('Hello from the procedure!');
  WriteLn('This is line 2');
end;

begin
  WriteLn('Program starting...');
  SayHello;  // Call the procedure
  WriteLn('Program ending...');
end.

program ReuseProcedure;

procedure PrintSeparator;
begin
  WriteLn('--------------------');
end;

begin
  WriteLn('Section 1');
  PrintSeparator;
  WriteLn('Section 2');
  PrintSeparator;
  WriteLn('Section 3');
  PrintSeparator;
end.

program MultipleProcedures;

procedure ShowHeader;
begin
  WriteLn('====================');
  WriteLn('  My Program v1.0');
  WriteLn('====================');
end;

procedure ShowFooter;
begin
  WriteLn('====================');
  WriteLn('  End of Program');
  WriteLn('====================');
end;

begin
  ShowHeader;
  WriteLn('');
  WriteLn('Program content here...');
  WriteLn('');
  ShowFooter;
end.

