exercises.solution:
program MenuSystem;

procedure ShowMainMenu;
begin
  WriteLn('=== Main Menu ===');
  WriteLn('1. Start');
  WriteLn('2. Help');
  WriteLn('3. About');
  WriteLn('4. Exit');
end;

procedure ShowHelp;
begin
  WriteLn('=== Help ===');
  WriteLn('Use the menu to navigate.');
  WriteLn('Press numbers to select options.');
end;

procedure ShowAbout;
begin
  WriteLn('=== About ===');
  WriteLn('Program: Menu System v1.0');
  WriteLn('Author: Student');
end;

begin
  ShowMainMenu;
  WriteLn('');
  ShowHelp;
  WriteLn('');
  ShowAbout;
end.
