program BasicOverloading;

function Max(a, b: Integer): Integer; overload;
begin
  if a > b then
    Result := a
  else
    Result := b;
end;

function Max(a, b: Float): Float; overload;
begin
  if a > b then
    Result := a
  else
    Result := b;
end;

begin
  var intMax := Max(5, 10);        // Calls Integer version
  var floatMax := Max(3.5, 2.7);   // Calls Float version

  WriteLn('Max integers: ' + IntToStr(intMax));
  WriteLn('Max floats: ' + FloatToStr(floatMax));
end.

program ParamCountOverload;

function Sum(a, b: Integer): Integer; overload;
begin
  Result := a + b;
end;

function Sum(a, b, c: Integer): Integer; overload;
begin
  Result := a + b + c;
end;

function Sum(a, b, c, d: Integer): Integer; overload;
begin
  Result := a + b + c + d;
end;

begin
  WriteLn('Sum(5, 10) = ' + IntToStr(Sum(5, 10)));
  WriteLn('Sum(5, 10, 15) = ' + IntToStr(Sum(5, 10, 15)));
  WriteLn('Sum(5, 10, 15, 20) = ' + IntToStr(Sum(5, 10, 15, 20)));
end.

program PrintOverload;

procedure Print(value: Integer); overload;
begin
  WriteLn('Integer: ' + IntToStr(value));
end;

procedure Print(value: Float); overload;
begin
  WriteLn('Float: ' + FloatToStr(value));
end;

procedure Print(value: String); overload;
begin
  WriteLn('String: ' + value);
end;

procedure Print(value: Boolean); overload;
begin
  if value then
    WriteLn('Boolean: True')
  else
    WriteLn('Boolean: False');
end;

begin
  Print(42);
  Print(3.14);
  Print('Hello!');
  Print(true);
end.

program DistanceOverload;

// Distance from origin to point (x, y)
function Distance(x, y: Float): Float; overload;
begin
  Result := Sqrt(x*x + y*y);
end;

// Distance between two points
function Distance(x1, y1, x2, y2: Float): Float; overload;
begin
  var dx := x2 - x1;
  var dy := y2 - y1;
  Result := Sqrt(dx*dx + dy*dy);
end;

begin
  WriteLn('Distance from origin to (3,4): ' + FloatToStr(Distance(3.0, 4.0)));
  WriteLn('Distance between (1,2) and (4,6): ' + FloatToStr(Distance(1.0, 2.0, 4.0, 6.0)));
end.

program FormatOverload;

function FormatMoney(amount: Float): String; overload;
begin
  Result := '$' + FloatToStr(amount);
end;

function FormatMoney(amount: Float; currency: String): String; overload;
begin
  Result := currency + FloatToStr(amount);
end;

function FormatMoney(amount: Float; currency: String; decimals: Integer): String; overload;
begin
  // Simple formatting - in practice you'd use proper decimal formatting
  Result := currency + FloatToStr(amount);
end;

begin
  WriteLn(FormatMoney(42.50));
  WriteLn(FormatMoney(42.50, '€'));
  WriteLn(FormatMoney(42.50, '£', 2));
end.

