exercises.solution:
program MultiplyOverload;

function Multiply(a, b: Integer): Integer; overload;
begin
  Result := a * b;
end;

function Multiply(a, b, c: Integer): Integer; overload;
begin
  Result := a * b * c;
end;

function Multiply(a, b: Float): Float; overload;
begin
  Result := a * b;
end;

begin
  WriteLn('2 * 3 = ' + IntToStr(Multiply(2, 3)));
  WriteLn('2 * 3 * 4 = ' + IntToStr(Multiply(2, 3, 4)));
  WriteLn('2.5 * 3.2 = ' + FloatToStr(Multiply(2.5, 3.2)));
end.

