program AreaCalculator;

// Define three Area functions
// Square: side * side
// Rectangle: width * height
// Circle: π * radius²

begin
  WriteLn('Square (5): ' + FloatToStr(Area(5.0)));
  WriteLn('Rectangle (4,6): ' + FloatToStr(Area(4.0, 6.0)));
  WriteLn('Circle (3): ' + FloatToStr(AreaCircle(3.0)));
end.

exercises.solution:
program AreaCalculator;

function Area(side: Float): Float; overload;
begin
  Result := side * side;
end;

function Area(width, height: Float): Float; overload;
begin
  Result := width * height;
end;

function AreaCircle(radius: Float): Float;
begin
  const PI = 3.14159;
  Result := PI * radius * radius;
end;

begin
  WriteLn('Square (5): ' + FloatToStr(Area(5.0)));
  WriteLn('Rectangle (4,6): ' + FloatToStr(Area(4.0, 6.0)));
  WriteLn('Circle (3): ' + FloatToStr(AreaCircle(3.0)));
end.

