exercises.solution:
program GreetingOverload;

procedure Greet(name: String); overload;
begin
  WriteLn('Hello, ' + name + '!');
end;

procedure Greet(name, title: String); overload;
begin
  WriteLn('Hello, ' + title + ' ' + name + '!');
end;

procedure Greet(name, title, greeting: String); overload;
begin
  WriteLn(greeting + ', ' + title + ' ' + name + '!');
end;

begin
  Greet('Alice');
  Greet('Bob', 'Mr.');
  Greet('Carol', 'Dr.', 'Welcome');
end.
