program StaticArrayExample;

var
  numbers: array[1..5] of Integer;
  i: Integer;

begin
  // Assign values to array elements
  numbers[1] := 10;
  numbers[2] := 20;
  numbers[3] := 30;
  numbers[4] := 40;
  numbers[5] := 50;

  // Display array elements
  for i := 1 to 5 do
    WriteLn('numbers[', i, '] = ', numbers[i]);
end.

program DynamicArrayExample;

var
  scores: array of Integer;
  i: Integer;

begin
  // Set the size of the dynamic array
  SetLength(scores, 3);

  // Assign values (indices start at 0)
  scores[0] := 85;
  scores[1] := 92;
  scores[2] := 78;

  // Display all elements
  WriteLn('Number of scores: ', Length(scores));
  for i := 0 to Length(scores) - 1 do
    WriteLn('Score ', i + 1, ': ', scores[i]);

  // Calculate average
  var sum := 0;
  for i := 0 to Length(scores) - 1 do
    sum := sum + scores[i];
  WriteLn('Average: ', sum / Length(scores):0:2);
end.

program StringArrayExample;

var
  days: array[0..6] of String;
  i: Integer;

begin
  // Initialize days of the week
  days[0] := 'Monday';
  days[1] := 'Tuesday';
  days[2] := 'Wednesday';
  days[3] := 'Thursday';
  days[4] := 'Friday';
  days[5] := 'Saturday';
  days[6] := 'Sunday';

  WriteLn('Days of the week:');
  for i := 0 to 6 do
    WriteLn((i + 1), '. ', days[i]);
end.

program MultiDimensionalArray;

var
  grid: array[1..3, 1..3] of Integer;
  row, col: Integer;
  value: Integer;

begin
  // Fill the grid
  value := 1;
  for row := 1 to 3 do
    for col := 1 to 3 do
    begin
      grid[row, col] := value;
      value := value + 1;
    end;

  // Display the grid
  WriteLn('3x3 Grid:');
  for row := 1 to 3 do
  begin
    for col := 1 to 3 do
      Write(grid[row, col]:3);
    WriteLn;
  end;
end.

