exercises.solution:
program TemperatureTracker;

var
  temps: array[1..7] of Real;
  i: Integer;
  highest, lowest, sum, average: Real;

begin
  // Assign temperature values
  temps[1] := 22.5;
  temps[2] := 24.0;
  temps[3] := 21.5;
  temps[4] := 23.5;
  temps[5] := 25.0;
  temps[6] := 26.5;
  temps[7] := 24.5;

  // Initialize with first value
  highest := temps[1];
  lowest := temps[1];
  sum := 0;

  // Find highest, lowest, and sum
  for i := 1 to 7 do
  begin
    if temps[i] > highest then
      highest := temps[i];
    if temps[i] < lowest then
      lowest := temps[i];
    sum := sum + temps[i];
  end;

  average := sum / 7;

  WriteLn('Week Temperature Summary:');
  WriteLn('Highest: ', highest:0:1, '°C');
  WriteLn('Lowest: ', lowest:0:1, '°C');
  WriteLn('Average: ', average:0:1, '°C');
end.

