program ShoppingList;

var
  items: array of String;
  i: Integer;

begin
  // Set array size
  SetLength(items, 5);

  // Add items
  items[0] := 'Milk';
  items[1] := 'Bread';
  items[2] := 'Eggs';
  items[3] := 'Butter';
  items[4] := 'Cheese';

  // Display the shopping list
  WriteLn('Shopping List:');
  for i := 0 to Length(items) - 1 do
    WriteLn((i + 1), '. ', items[i]);

  WriteLn;
  WriteLn('Total items: ', Length(items));
end.

