exercises.solution:
program ReverseArray;

var
  numbers: array[1..5] of Integer;
  i: Integer;

begin
  numbers[1] := 10;
  numbers[2] := 20;
  numbers[3] := 30;
  numbers[4] := 40;
  numbers[5] := 50;

  WriteLn('Original order:');
  for i := 1 to 5 do
    Write(numbers[i], ' ');
  WriteLn;

  WriteLn('Reverse order:');
  for i := 5 downto 1 do
    Write(numbers[i], ' ');
  WriteLn;
end.
