program SimpleRecord;

type
  TPerson = record
    Name: String;
    Age: Integer;
    City: String;
  end;

var
  person: TPerson;

begin
  // Assign values to record fields
  person.Name := 'Alice';
  person.Age := 25;
  person.City := 'New York';

  // Display record data
  WriteLn('Person Information:');
  WriteLn('Name: ', person.Name);
  WriteLn('Age: ', person.Age);
  WriteLn('City: ', person.City);
end.

program ProductRecord;

type
  TProduct = record
    Name: String;
    Price: Real;
    InStock: Boolean;
    Quantity: Integer;
  end;

var
  product: TProduct;

begin
  product.Name := 'Laptop';
  product.Price := 899.99;
  product.InStock := True;
  product.Quantity := 15;

  WriteLn('Product: ', product.Name);
  WriteLn('Price: $', product.Price:0:2);
  WriteLn('In Stock: ', product.InStock);
  WriteLn('Quantity: ', product.Quantity, ' units');

  // Calculate total value
  var totalValue := product.Price * product.Quantity;
  WriteLn('Total Inventory Value: $', totalValue:0:2);
end.

program StudentRecords;

type
  TStudent = record
    Name: String;
    Grade: Integer;
    Score: Real;
  end;

var
  students: array[1..3] of TStudent;
  i: Integer;
  totalScore: Real;

begin
  // Initialize student data
  students[1].Name := 'Bob';
  students[1].Grade := 10;
  students[1].Score := 85.5;

  students[2].Name := 'Carol';
  students[2].Grade := 10;
  students[2].Score := 92.0;

  students[3].Name := 'David';
  students[3].Grade := 10;
  students[3].Score := 78.5;

  // Display all students
  WriteLn('Student Roster:');
  WriteLn('-----------------------------------');
  totalScore := 0;
  for i := 1 to 3 do
  begin
    WriteLn(students[i].Name, ' (Grade ', students[i].Grade, '): ', 
            students[i].Score:0:1);
    totalScore := totalScore + students[i].Score;
  end;

  WriteLn('-----------------------------------');
  WriteLn('Class Average: ', (totalScore / 3):0:2);
end.

program NestedRecords;

type
  TAddress = record
    Street: String;
    City: String;
    ZipCode: String;
  end;

  TEmployee = record
    Name: String;
    ID: Integer;
    Salary: Real;
    Address: TAddress;
  end;

var
  employee: TEmployee;

begin
  employee.Name := 'John Smith';
  employee.ID := 1001;
  employee.Salary := 55000.00;

  employee.Address.Street := '123 Main St';
  employee.Address.City := 'Boston';
  employee.Address.ZipCode := '02101';

  WriteLn('Employee Information:');
  WriteLn('Name: ', employee.Name);
  WriteLn('ID: ', employee.ID);
  WriteLn('Salary: $', employee.Salary:0:2);
  WriteLn('Address: ', employee.Address.Street);
  WriteLn('         ', employee.Address.City, ', ', employee.Address.ZipCode);
end.

