exercises.solution:
program PointDistance;

type
  TPoint = record
    X: Real;
    Y: Real;
  end;

var
  point1, point2: TPoint;
  distance: Real;

begin
  point1.X := 0;
  point1.Y := 0;
  point2.X := 3;
  point2.Y := 4;

  WriteLn('Point 1: (', point1.X:0:0, ', ', point1.Y:0:0, ')');
  WriteLn('Point 2: (', point2.X:0:0, ', ', point2.Y:0:0, ')');

  // Calculate distance
  var dx := point2.X - point1.X;
  var dy := point2.Y - point1.Y;
  distance := Sqrt(dx * dx + dy * dy);

  WriteLn('Distance: ', distance:0:2);
end.

