exercises.solution:
program MovieCollection;

type
  TMovie = record
    Title: String;
    Director: String;
    Year: Integer;
    Rating: Real;
  end;

var
  movies: array[1..3] of TMovie;
  i, bestIndex: Integer;

begin
  movies[1].Title := 'Inception';
  movies[1].Director := 'Christopher Nolan';
  movies[1].Year := 2010;
  movies[1].Rating := 8.8;

  movies[2].Title := 'The Matrix';
  movies[2].Director := 'Wachowski Brothers';
  movies[2].Year := 1999;
  movies[2].Rating := 8.7;

  movies[3].Title := 'Interstellar';
  movies[3].Director := 'Christopher Nolan';
  movies[3].Year := 2014;
  movies[3].Rating := 8.6;

  WriteLn('Movie Collection:');
  WriteLn;

  bestIndex := 1;
  for i := 1 to 3 do
  begin
    WriteLn(movies[i].Title, ' (', movies[i].Year, ')');
    WriteLn('Director: ', movies[i].Director);
    WriteLn('Rating: ', movies[i].Rating:0:1, '/10');
    WriteLn;

    if movies[i].Rating > movies[bestIndex].Rating then
      bestIndex := i;
  end;

  WriteLn('Highest Rated: ', movies[bestIndex].Title);
end.
