program BasicSets;

var
  numbers: set of 1..10;
  digit: Integer;

begin
  // Initialize a set
  numbers := [2, 4, 6, 8, 10];

  WriteLn('Even numbers from 1-10: [2, 4, 6, 8, 10]');
  WriteLn;

  // Check membership
  digit := 4;
  if digit in numbers then
    WriteLn(digit, ' is in the set')
  else
    WriteLn(digit, ' is not in the set');

  digit := 5;
  if digit in numbers then
    WriteLn(digit, ' is in the set')
  else
    WriteLn(digit, ' is not in the set');
end.

program SetOperations;

var
  setA, setB, unionSet, intersectionSet: set of 1..10;

begin
  setA := [1, 2, 3, 4, 5];
  setB := [4, 5, 6, 7, 8];

  WriteLn('Set A: [1, 2, 3, 4, 5]');
  WriteLn('Set B: [4, 5, 6, 7, 8]');
  WriteLn;

  // Union: all elements from both sets
  unionSet := setA + setB;
  WriteLn('Union (A + B): [1, 2, 3, 4, 5, 6, 7, 8]');

  // Intersection: elements in both sets
  intersectionSet := setA * setB;
  WriteLn('Intersection (A * B): [4, 5]');

  // Check results
  if 1 in unionSet then
    WriteLn('1 is in the union');
  if 4 in intersectionSet then
    WriteLn('4 is in the intersection');
end.

program CharacterSets;

