program StoreHours;

type
  TDay = (Sunday, Monday, Tuesday, Wednesday, Thursday, Friday, Saturday);

var
  openDays: set of TDay;
  today: TDay;

begin
  // Set the open days
  openDays := [Monday, Wednesday, Friday, Saturday];

  WriteLn('Store Hours Checker');
  WriteLn('-------------------');
  WriteLn('Open: Monday, Wednesday, Friday, Saturday');
  WriteLn;

  // Check different days
  today := Monday;
  if today in openDays then
    WriteLn('Monday: OPEN')
  else
    WriteLn('Monday: CLOSED');

  today := Tuesday;
  if today in openDays then
    WriteLn('Tuesday: OPEN')
  else
    WriteLn('Tuesday: CLOSED');

  today := Friday;
  if today in openDays then
    WriteLn('Friday: OPEN')
  else
    WriteLn('Friday: CLOSED');
end.

