program Permissions;

type
  TPermission = (Read, Write, Execute);

var
  userPerms, adminPerms, commonPerms: set of TPermission;

begin
  // Set permissions
  userPerms := [Read, Execute];
  adminPerms := [Read, Write, Execute];

  WriteLn('Permission System');
  WriteLn('----------------');

  // Check user permissions
  WriteLn('User permissions:');
  if Read in userPerms then WriteLn('  - Read: YES');
  if Write in userPerms then 
    WriteLn('  - Write: YES')
  else
    WriteLn('  - Write: NO');
  if Execute in userPerms then WriteLn('  - Execute: YES');

  WriteLn;
  WriteLn('Admin permissions:');
  if Read in adminPerms then WriteLn('  - Read: YES');
  if Write in adminPerms then WriteLn('  - Write: YES');
  if Execute in adminPerms then WriteLn('  - Execute: YES');

  WriteLn;
  // Find common permissions
  commonPerms := userPerms * adminPerms;
  if Read in commonPerms then
    WriteLn('Both have Read permission');
end.
