program DaysOfWeek;

type
  TDay = (Monday, Tuesday, Wednesday, Thursday, Friday, Saturday, Sunday);

var
  today: TDay;
  tomorrow: TDay;

begin
  today := Wednesday;

  WriteLn('Today is Wednesday');
  WriteLn('Ordinal value: ', Ord(today));

  // Get next day
  if today <> Sunday then
    tomorrow := Succ(today)
  else
    tomorrow := Monday;

  WriteLn('Tomorrow is Thursday (ordinal: ', Ord(tomorrow), ')');

  // Comparison
  if today < Friday then
    WriteLn('Still a few days until Friday!');
end.

program TrafficLight;

type
  TLightColor = (Red, Yellow, Green);

var
  light: TLightColor;
  nextLight: TLightColor;

begin
  light := Red;

  WriteLn('Current light: Red');

  // Determine action based on light
  case light of
    Red:
    begin
      WriteLn('Action: STOP');
      nextLight := Green;
    end;
    Yellow:
    begin
      WriteLn('Action: Prepare to stop');
      nextLight := Red;
    end;
    Green:
    begin
      WriteLn('Action: GO');
      nextLight := Yellow;
    end;
  end;

  WriteLn('Next light will be: Green');
end.

program GameDirection;

type
  TDirection = (North, East, South, West);

var
  facing: TDirection;
  x, y: Integer;

begin
  x := 0;
  y := 0;
  facing := North;

  WriteLn('Starting position: (', x, ', ', y, ')');
  WriteLn('Facing: North');
  WriteLn;

  // Move based on direction
  case facing of
    North: y := y + 1;
    South: y := y - 1;
    East: x := x + 1;
    West: x := x - 1;
  end;

  WriteLn('After moving north:');
  WriteLn('New position: (', x, ', ', y, ')');

  // Turn right (clockwise)
  facing := East;
  WriteLn('After turning right: Facing East');

  case facing of
    North: y := y + 1;
    South: y := y - 1;
    East: x := x + 1;
    West: x := x - 1;
  end;

  WriteLn('After moving east:');
  WriteLn('Final position: (', x, ', ', y, ')');
end.

program PrioritySystem;

type
  TPriority = (Low, Medium, High, Critical);

  TTask = record
    Name: String;
    Priority: TPriority;
  end;

var
  task1, task2: TTask;

begin
  task1.Name := 'Write documentation';
  task1.Priority := Medium;

  task2.Name := 'Fix security bug';
  task2.Priority := Critical;

  WriteLn('Task 1: ', task1.Name);
  WriteLn('Priority: Medium (level ', Ord(task1.Priority), ')');
  WriteLn;

  WriteLn('Task 2: ', task2.Name);
  WriteLn('Priority: Critical (level ', Ord(task2.Priority), ')');
  WriteLn;

  // Compare priorities
  if task2.Priority > task1.Priority then
    WriteLn('Task 2 has higher priority - do it first!');
end.

program FilePermissions;

type
  TPermission = (Read, Write, Execute, Delete);
  TPermissionSet = set of TPermission;

var
  userPerms, adminPerms: TPermissionSet;

begin
  // Set permissions
  userPerms := [Read, Write];
  adminPerms := [Read, Write, Execute, Delete];

  WriteLn('User Permissions:');
  if Read in userPerms then WriteLn('  - Read');
  if Write in userPerms then WriteLn('  - Write');
  if Execute in userPerms then 
    WriteLn('  - Execute')
  else
    WriteLn('  - Execute: NO');
  if Delete in userPerms then 
    WriteLn('  - Delete')
  else
    WriteLn('  - Delete: NO');

  WriteLn;
  WriteLn('Admin Permissions:');
  if Read in adminPerms then WriteLn('  - Read');
  if Write in adminPerms then WriteLn('  - Write');
  if Execute in adminPerms then WriteLn('  - Execute');
  if Delete in adminPerms then WriteLn('  - Delete');
end.

program EnumIteration;

type
  TSeason = (Spring, Summer, Fall, Winter);

var
  season: TSeason;
  i: Integer;

begin
  WriteLn('Seasons of the year:');
  WriteLn;

  for i := Ord(Spring) to Ord(Winter) do
  begin
    season := TSeason(i);

    Write('Season ', i + 1, ': ');
    case season of
      Spring: WriteLn('Spring (Ordinal: ', Ord(season), ')');
      Summer: WriteLn('Summer (Ordinal: ', Ord(season), ')');
      Fall: WriteLn('Fall (Ordinal: ', Ord(season), ')');
      Winter: WriteLn('Winter (Ordinal: ', Ord(season), ')');
    end;
  end;
end.

