program MonthDays;

type
  TMonth = (Jan, Feb, Mar, Apr, May, Jun, Jul, Aug, Sep, Oct, Nov, Dec);

var
  month: TMonth;
  days: Integer;

begin
  month := Feb;

  // Determine number of days
  case month of
    Jan, Mar, May, Jul, Aug, Oct, Dec: days := 31;
    Apr, Jun, Sep, Nov: days := 30;
    Feb: days := 28;  // Not accounting for leap years
  end;

  WriteLn('Month: February');
  WriteLn('Days: ', days);
  WriteLn;

  // Test with another month
  month := Jul;
  case month of
    Jan, Mar, May, Jul, Aug, Oct, Dec: days := 31;
    Apr, Jun, Sep, Nov: days := 30;
    Feb: days := 28;
  end;

  WriteLn('Month: July');
  WriteLn('Days: ', days);
end.

