program PlayingCard;

type
  TSuit = (Clubs, Diamonds, Hearts, Spades);
  TRank = (Two, Three, Four, Five, Six, Seven, Eight, Nine, Ten, Jack, Queen, King, Ace);

var
  suit: TSuit;
  rank: TRank;
  color: String;

begin
  suit := Hearts;
  rank := Ace;

  // Determine color
  case suit of
    Clubs, Spades: color := 'Black';
    Diamonds, Hearts: color := 'Red';
  end;

  Write('Card: ');
  case rank of
    Two: Write('2');
    Three: Write('3');
    Four: Write('4');
    Five: Write('5');
    Six: Write('6');
    Seven: Write('7');
    Eight: Write('8');
    Nine: Write('9');
    Ten: Write('10');
    Jack: Write('Jack');
    Queen: Write('Queen');
    King: Write('King');
    Ace: Write('Ace');
  end;

  Write(' of ');
  case suit of
    Clubs: Write('Clubs');
    Diamonds: Write('Diamonds');
    Hearts: Write('Hearts');
    Spades: Write('Spades');
  end;

  WriteLn;
  WriteLn('Color: ', color);
end.

