program OrderTracker;

type
  TOrderStatus = (Pending, Processing, Shipped, Delivered);

var
  status: TOrderStatus;
  stepNum: Integer;

begin
  status := Pending;

  WriteLn('Order Status Tracker');
  WriteLn('===================');
  WriteLn;

  // Process through all states
  stepNum := 1;
  while status <= Delivered do
  begin
    Write('Step ', stepNum, ': ');

    case status of
      Pending:
      begin
        WriteLn('Order Pending');
        WriteLn('  Waiting for confirmation...');
      end;
      Processing:
      begin
        WriteLn('Order Processing');
        WriteLn('  Preparing your order...');
      end;
      Shipped:
      begin
        WriteLn('Order Shipped');
        WriteLn('  On the way to you...');
      end;
      Delivered:
      begin
        WriteLn('Order Delivered');
        WriteLn('  Enjoy your purchase!');
      end;
    end;

    WriteLn;

    if status < Delivered then
      status := Succ(status)
    else
      break;

    stepNum := stepNum + 1;
  end;
end.
