program SimpleLine;

begin
  SetPenColor('#0066cc');
  SetPenWidth(2);

  Forward(100);

  WriteLn('Drew a line!');
end.

program DrawSquare;

var
  i: Integer;

begin
  SetPenColor('#00AA00');
  SetPenWidth(2);

  for i := 1 to 4 do
  begin
    Forward(100);
    TurnRight(90);
  end;

  WriteLn('Square drawn!');
end.

program DrawTriangle;

var
  i: Integer;

begin
  SetPenColor('#FF6600');
  SetPenWidth(3);

  for i := 1 to 3 do
  begin
    Forward(120);
    TurnRight(120);
  end;

  WriteLn('Triangle complete!');
end.

