exercises.solution:
program DrawRectangle;

var
  i: Integer;

begin
  SetPenColor('#9900CC');
  SetPenWidth(2);

  for i := 1 to 2 do
  begin
    Forward(150);  // Long side
    TurnRight(90);
    Forward(80);   // Short side
    TurnRight(90);
  end;

  WriteLn('Rectangle drawn!');
end.

